mapper.select:

SELECT R.R_NUM, R.ID, R.TITLE, R.CONTENT, R.VIEWS,
       R.CREATED, R.NAME AS staffName
FROM (SELECT ROWNUM R_NUM, N.*
      FROM (SELECT N.*, S.NAME
            FROM NOTICE N
            JOIN STAFF S ON N.STAFF_ID = S.ID
            ORDER BY N.ID DESC
            )N
      WHERE ROWNUM <= #{end}
      )R
WHERE R.R_NUM >= #{begin}

mapper.update:
UPDATE NOTICE
SET VIEWS = VIEWS + 1
WHERE ID = #{id}

UPDATE NOTICE 
SET TITLE = #{title},
    CONTENT = #{content}
WHERE ID = #{id}
